import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import { Typography, Button, List, ListItem, ListItemText, Paper, TextField, Dialog, DialogActions, DialogContent, DialogTitle, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const VitalsScreen = () => {
  const [vitals, setVitals] = useState([]);
  const [open, setOpen] = useState(false);
  const [newVital, setNewVital] = useState({ type: '', value: '' });
  const [chartData, setChartData] = useState(null);
  const [error, setError] = useState('');

  const getRandomColor = () => {
    return '#' + Math.floor(Math.random()*16777215).toString(16);
  };

  const prepareChartData = useCallback(() => {
    const vitalTypes = [...new Set(vitals.map(vital => vital.type))];
    const datasets = vitalTypes.map(type => {
      const data = vitals
        .filter(vital => vital.type === type)
        .sort((a, b) => new Date(a.date) - new Date(b.date))
        .map(vital => ({ x: new Date(vital.date), y: parseFloat(vital.value) }));
      return {
        label: type,
        data: data,
        borderColor: getRandomColor(),
        fill: false
      };
    });

    setChartData({
      datasets: datasets
    });
  }, [vitals]);

  useEffect(() => {
    fetchVitals();
  }, []);

  useEffect(() => {
    prepareChartData();
  }, [vitals, prepareChartData]);

  const fetchVitals = async () => {
    try {
      const response = await api.getVitals();
      setVitals(response.data);
    } catch (error) {
      console.error('Error fetching vitals:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (e) => {
    setNewVital({ ...newVital, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (!newVital.type || !newVital.value) {
      setError('Please fill in both fields');
      return;
    }
    if (isNaN(parseFloat(newVital.value))) {
      setError('Value must be a number');
      return;
    }
    try {
      console.log('Submitting vital:', newVital);
      const response = await api.addVital(newVital);
      console.log('Vital added:', response.data);
      setOpen(false);
      setError('');
      fetchVitals();
    } catch (error) {
      console.error('Error adding vital:', error.response ? error.response.data : error.message);
      setError('Failed to add vital. Please try again.');
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.deleteVital(id);
      fetchVitals();
    } catch (error) {
      console.error('Error deleting vital:', error);
    }
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Vitals Over Time',
      },
    },
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day'
        }
      }
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Vitals Tracking
      </Typography>
      {chartData && (
        <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
          <Line data={chartData} options={chartOptions} />
        </Paper>
      )}
      <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
        {vitals.length > 0 ? (
          <List>
            {vitals.map((vital) => (
              <ListItem key={vital.id} secondaryAction={
                <IconButton edge="end" aria-label="delete" onClick={() => handleDelete(vital.id)}>
                  <DeleteIcon />
                </IconButton>
              }>
                <ListItemText
                  primary={`${vital.type}: ${vital.value}`}
                  secondary={new Date(vital.date).toLocaleString()}
                />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography>No vitals recorded yet.</Typography>
        )}
      </Paper>
      <Button variant="contained" onClick={handleClickOpen}>Add Vital</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add New Vital</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="type"
            label="Vital Type"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="value"
            label="Value"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
          {error && <Typography color="error">{error}</Typography>}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>Add</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default VitalsScreen;
