import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { Typography, Button, List, ListItem, ListItemText, Paper, TextField, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';

const VitalsScreen = () => {
  const [vitals, setVitals] = useState([]);
  const [open, setOpen] = useState(false);
  const [newVital, setNewVital] = useState({ type: '', value: '' });

  useEffect(() => {
    fetchVitals();
  }, []);

  const fetchVitals = async () => {
    try {
      const response = await api.getVitals();
      setVitals(response.data);
    } catch (error) {
      console.error('Error fetching vitals:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (e) => {
    setNewVital({ ...newVital, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      console.log('Submitting vital:', newVital);
      const response = await api.addVital(newVital);
      console.log('Vital added:', response.data);
      setOpen(false);
      fetchVitals();
    } catch (error) {
      console.error('Error adding vital:', error.response ? error.response.data : error.message);
      // You can add an alert or snackbar here to show the error to the user
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Vitals Tracking
      </Typography>
      <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
        {vitals.length > 0 ? (
          <List>
            {vitals.map((vital) => (
              <ListItem key={vital.id}>
                <ListItemText
                  primary={`${vital.type}: ${vital.value}`}
                  secondary={new Date(vital.date).toLocaleString()}
                />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography>No vitals recorded yet.</Typography>
        )}
      </Paper>
      <Button variant="contained" onClick={handleClickOpen}>Add Vital</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add New Vital</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="type"
            label="Vital Type"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="value"
            label="Value"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>Add</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default VitalsScreen;
