import React, { useState, useEffect } from 'react';
import api from '../services/api';

const VitalsScreen = () => {
  const [vitals, setVitals] = useState([]);

  useEffect(() => {
    fetchVitals();
  }, []);

  const fetchVitals = async () => {
    try {
      const response = await api.getVitals();
      setVitals(response.data);
    } catch (error) {
      console.error('Error fetching vitals:', error);
    }
  };

  return (
    <div className="vitals-screen">
      <h1>Vitals Tracking</h1>
      {vitals.length > 0 ? (
        <ul>
          {vitals.map((vital) => (
            <li key={vital.id} className="vital-item">
              <p className="vital-type">{vital.type}</p>
              <p className="vital-value">{vital.value}</p>
              <p className="vital-date">{new Date(vital.date).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No vitals recorded yet.</p>
      )}
      <button className="add-button">Add Vital</button>
    </div>
  );
};

export default VitalsScreen;
