import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { Typography, Button, List, ListItem, ListItemText, Paper, TextField, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';

const AppointmentsScreen = () => {
  const [appointments, setAppointments] = useState([]);
  const [open, setOpen] = useState(false);
  const [newAppointment, setNewAppointment] = useState({ date: '', time: '', doctor: '', reason: '' });

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await api.getAppointments();
      setAppointments(response.data);
    } catch (error) {
      console.error('Error fetching appointments:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (e) => {
    setNewAppointment({ ...newAppointment, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      console.log('Submitting appointment:', newAppointment);
      const response = await api.createAppointment(newAppointment);
      console.log('Appointment created:', response.data);
      setOpen(false);
      fetchAppointments();
    } catch (error) {
      console.error('Error creating appointment:', error.response ? error.response.data : error.message);
      // You can add an alert or snackbar here to show the error to the user
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Appointments
      </Typography>
      <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
        {appointments.length > 0 ? (
          <List>
            {appointments.map((appointment) => (
              <ListItem key={appointment.id}>
                <ListItemText
                  primary={`Dr. ${appointment.doctor}`}
                  secondary={`${appointment.date} at ${appointment.time} - ${appointment.reason}`}
                />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography>No upcoming appointments.</Typography>
        )}
      </Paper>
      <Button variant="contained" onClick={handleClickOpen}>Add Appointment</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add New Appointment</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="date"
            label="Date"
            type="date"
            fullWidth
            variant="standard"
            InputLabelProps={{ shrink: true }}
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="time"
            label="Time"
            type="time"
            fullWidth
            variant="standard"
            InputLabelProps={{ shrink: true }}
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="doctor"
            label="Doctor"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="reason"
            label="Reason"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>Add</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AppointmentsScreen;
