import React, { useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import { Typography, Button, List, ListItem, ListItemText, Paper, TextField, Dialog, DialogActions, DialogContent, DialogTitle, IconButton, Grid } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';
import SentimentDissatisfiedIcon from '@mui/icons-material/SentimentDissatisfied';
import SentimentSatisfiedIcon from '@mui/icons-material/SentimentSatisfied';
import SentimentSatisfiedAltIcon from '@mui/icons-material/SentimentSatisfiedAlt';
import SentimentVerySatisfiedIcon from '@mui/icons-material/SentimentVerySatisfied';

const moodIcons = [
  { icon: <SentimentVeryDissatisfiedIcon />, label: 'Very Bad' },
  { icon: <SentimentDissatisfiedIcon />, label: 'Bad' },
  { icon: <SentimentSatisfiedIcon />, label: 'Okay' },
  { icon: <SentimentSatisfiedAltIcon />, label: 'Good' },
  { icon: <SentimentVerySatisfiedIcon />, label: 'Very Good' },
];

const MoodTracker = ({ onMoodSelect }) => {
  return (
    <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
      <Typography variant="h6" gutterBottom>How are you feeling today?</Typography>
      <Grid container spacing={2} justifyContent="center">
        {moodIcons.map((mood, index) => (
          <Grid item key={index}>
            <IconButton onClick={() => onMoodSelect(index)} size="large" color="primary">
              {mood.icon}
            </IconButton>
            <Typography variant="caption">{mood.label}</Typography>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

const VitalsScreen = () => {
  const [vitals, setVitals] = useState([]);
  const [open, setOpen] = useState(false);
  const [newVital, setNewVital] = useState({ type: '', value: '' });
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState('');

  const prepareChartData = useCallback(() => {
    const data = vitals.map(vital => ({
      date: new Date(vital.date).toLocaleDateString(),
      [vital.type]: parseFloat(vital.value)
    }));
    setChartData(data);
  }, [vitals]);

  useEffect(() => {
    fetchVitals();
  }, []);

  useEffect(() => {
    prepareChartData();
  }, [vitals, prepareChartData]);

  const fetchVitals = async () => {
    try {
      const response = await api.getVitals();
      setVitals(response.data);
    } catch (error) {
      console.error('Error fetching vitals:', error);
    }
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (e) => {
    setNewVital({ ...newVital, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (!newVital.type || !newVital.value) {
      setError('Please fill in both fields');
      return;
    }
    if (isNaN(parseFloat(newVital.value))) {
      setError('Value must be a number');
      return;
    }
    try {
      console.log('Submitting vital:', newVital);
      const response = await api.addVital(newVital);
      console.log('Vital added:', response.data);
      setOpen(false);
      setError('');
      fetchVitals();
    } catch (error) {
      console.error('Error adding vital:', error.response ? error.response.data : error.message);
      setError('Failed to add vital. Please try again.');
    }
  };

  const handleDelete = async (id) => {
    try {
      await api.deleteVital(id);
      fetchVitals();
    } catch (error) {
      console.error('Error deleting vital:', error);
    }
  };

  const handleMoodSelect = async (moodIndex) => {
    try {
      const newVital = { type: 'Mood', value: moodIndex.toString() };
      await api.addVital(newVital);
      fetchVitals();
    } catch (error) {
      console.error('Error adding mood:', error);
    }
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Vitals Tracking
      </Typography>
      <MoodTracker onMoodSelect={handleMoodSelect} />
      {chartData.length > 0 && (
        <Paper elevation={3} sx={{ p: 2, mb: 2, height: 300 }}>
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Legend />
              {Object.keys(chartData[0]).filter(key => key !== 'date').map((key, index) => (
                <Line key={index} type="monotone" dataKey={key} stroke={`#${Math.floor(Math.random()*16777215).toString(16)}`} />
              ))}
            </LineChart>
          </ResponsiveContainer>
        </Paper>
      )}
      <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
        {vitals.length > 0 ? (
          <List>
            {vitals.map((vital) => (
              <ListItem key={vital.id} secondaryAction={
                <IconButton edge="end" aria-label="delete" onClick={() => handleDelete(vital.id)}>
                  <DeleteIcon />
                </IconButton>
              }>
                <ListItemText
                  primary={`${vital.type}: ${vital.value}`}
                  secondary={new Date(vital.date).toLocaleString()}
                />
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography>No vitals recorded yet.</Typography>
        )}
      </Paper>
      <Button variant="contained" onClick={handleClickOpen}>Add Vital</Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add New Vital</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="type"
            label="Vital Type"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="value"
            label="Value"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
          {error && <Typography color="error">{error}</Typography>}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>Add</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default VitalsScreen;
