import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import { ethers } from 'ethers';
import HomeScreen from './screens/HomeScreen';
import ChatScreen from './screens/ChatScreen';
import AppointmentsScreen from './screens/AppointmentsScreen';
import VitalsScreen from './screens/VitalsScreen';
import Sidebar from './components/Sidebar';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);

  const theme = createTheme({
    palette: {
      mode: darkMode ? 'dark' : 'light',
      primary: {
        main: '#4A90E2',
      },
      secondary: {
        main: '#50E3C2',
      },
    },
  });

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  useEffect(() => {
    const initializeEthereum = async () => {
      if (window.ethereum) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider(provider);

        try {
          await window.ethereum.request({ method: 'eth_requestAccounts' });
          const signer = provider.getSigner();
          const address = await signer.getAddress();
          setAccount(address);
        } catch (error) {
          console.error('User denied account access');
        }
      } else {
        console.log('Ethereum object not found, install MetaMask.');
      }
    };

    initializeEthereum();
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Box sx={{ display: 'flex', height: '100vh', overflow: 'hidden' }}>
          <Sidebar darkMode={darkMode} toggleDarkMode={toggleDarkMode} account={account} />
          <Box component="main" sx={{ flexGrow: 1, overflow: 'auto', position: 'relative' }}>
            <Routes>
              <Route path="/" element={<HomeScreen account={account} provider={provider} />} />
              <Route path="/chat" element={<ChatScreen />} />
              <Route path="/appointments" element={<AppointmentsScreen />} />
              <Route path="/vitals" element={<VitalsScreen />} />
            </Routes>
          </Box>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
