import React, { useState, useEffect } from 'react';
import api from '../services/api';

const AppointmentsScreen = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await api.getAppointments();
      setAppointments(response.data);
    } catch (error) {
      console.error('Error fetching appointments:', error);
    }
  };

  return (
    <div className="appointments-screen">
      <h1>Appointments</h1>
      {appointments.length > 0 ? (
        <ul>
          {appointments.map((appointment) => (
            <li key={appointment.id} className="appointment-item">
              <p className="appointment-date">{appointment.date} at {appointment.time}</p>
              <p className="appointment-doctor">Dr. {appointment.doctor}</p>
              <p className="appointment-reason">{appointment.reason}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No upcoming appointments.</p>
      )}
      <button className="add-button">Add Appointment</button>
    </div>
  );
};

export default AppointmentsScreen;
