import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Box, List, ListItem, ListItemIcon, ListItemText, Paper, Avatar, Typography, Switch } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import ChatIcon from '@mui/icons-material/Chat';
import EventIcon from '@mui/icons-material/Event';
import FavoriteIcon from '@mui/icons-material/Favorite';
import LocalHospitalIcon from '@mui/icons-material/LocalHospital';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import { styled } from '@mui/material/styles';

const StyledListItem = styled(ListItem)(({ theme, active }) => ({
  borderRadius: theme.shape.borderRadius,
  marginBottom: theme.spacing(1),
  backgroundColor: active ? theme.palette.action.selected : 'transparent',
  '&:hover': {
    backgroundColor: theme.palette.action.hover,
  },
}));

const Sidebar = ({ darkMode, toggleDarkMode }) => {
  const location = useLocation();

  const menuItems = [
    { text: 'Home', icon: <HomeIcon />, path: '/' },
    { text: 'Chat', icon: <ChatIcon />, path: '/chat' },
    { text: 'Appointments', icon: <EventIcon />, path: '/appointments' },
    { text: 'Vitals', icon: <FavoriteIcon />, path: '/vitals' },
  ];

  return (
    <Paper elevation={3} sx={{ width: 240, flexShrink: 0, height: '100%', overflow: 'auto', borderRadius: 0 }}>
      <Box sx={{ p: 2, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Avatar sx={{ width: 64, height: 64, mb: 1 }} src="/path-to-user-avatar.jpg" />
        <Typography variant="subtitle1">John Doe</Typography>
        <Typography variant="body2" color="text.secondary">john.doe@example.com</Typography>
      </Box>
      <List>
        {menuItems.map((item) => (
          <StyledListItem
            button
            key={item.text}
            component={Link}
            to={item.path}
            active={location.pathname === item.path ? 1 : 0}
          >
            <ListItemIcon>{item.icon}</ListItemIcon>
            <ListItemText primary={item.text} />
          </StyledListItem>
        ))}
      </List>
      <Box sx={{ mt: 'auto', p: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Brightness4Icon sx={{ mr: 1 }} />
          <Typography>Dark Mode</Typography>
        </Box>
        <Switch checked={darkMode} onChange={toggleDarkMode} />
      </Box>
    </Paper>
  );
};

export default Sidebar;
